mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tgod_albumcountinfo (view_count, sales, like_count, 
  profitTotal, modelProfitTotal, photographerProfitTotal, 
  siteProfitTotal, organProfitTotal, downcount
  )
values (#{viewCount,jdbcType=INTEGER}, #{sales,jdbcType=INTEGER}, #{likeCount,jdbcType=INTEGER}, 
  #{profittotal,jdbcType=DECIMAL}, #{modelprofittotal,jdbcType=DECIMAL}, #{photographerprofittotal,jdbcType=DECIMAL}, 
  #{siteprofittotal,jdbcType=DECIMAL}, #{organprofittotal,jdbcType=DECIMAL}, #{downcount,jdbcType=INTEGER}
  )

mapper.select:
SELECT T1.title, T1.status, T1.modelID, T1.modelName, T1.photographerID, T1.photographerName, T2.id, T2.view_count, T2.sales, T2.like_count, T2.profitTotal, T2.modelProfitTotal, T2.photographerProfitTotal, T2.siteProfitTotal, T2.organProfitTotal, T2.downcount, T2.click_count, T2.total_cost from tgod_photoalbum T1 left join tgod_albumcountinfo T2 on T1.id=T2.id
and T1.id =#{id}
and T1.title like '%${title}%'
and T1.modelID =#{modelid}
and T1.modelName like '%${modelname}%'
and T1.photographerID like '%${photographerid}%'
and T1.photographerName like '%${photographername}%'
and T1.type = #{type}
ORDER BY T1.id desc
limit #{start}, #{pageSize}

SELECT 
COUNT(1)
from tgod_photoalbum  T1


      and  T1.id =#{id}


        and T1.title like '%${title}%'



       and T1.modelID =#{modelid}


        and T1.modelName like '%${modelname}%'


       and T1.photographerID like '%${photographerid}%'


      and  T1.photographerName like '%${photographername}%'



        and T1.type = #{type}

SELECT T1.id, T1.like_count, T1.view_count, T1.downcount, T1.profitTotal, T1.modelProfitTotal, T1.photographerProfitTotal, (select count(T2.price) from tgod_orderProfit T2 where T2.product_type=2 and T1.id=T2.product_id) as downtotalPrice, (select count(T2.price) from tgod_orderProfit T2 where T2.product_type=1 and T1.id=T2.product_id) as viewtotalPrice, T1.profitTotal, T1.modelProfitTotal, T1.photographerProfitTotal, T1.siteProfitTotal
from tgod_albumcountinfo T1
T1.id =#{id}

SELECT T1.id as modelid, T1.nickname as modelname, (select count(1) from tgod_photoalbum T2 where T2.modelID=T1.id and T2.type<>1
and T2.publishTime >=date_format(#{fromTime},'%Y-%m-%d %H:%i:%s')
and T2.publishTime <=date_format(#{toTime},'%Y-%m-%d %H:%i:%s')
) as dynamicCount, (select count(1) from tgod_photoalbum T2 where T2.modelID=T1.id and T2.type=3 and T2.status=1
and T2.publishTime >=date_format(#{fromTime},'%Y-%m-%d %H:%i:%s')
and T2.publishTime <=date_format(#{toTime},'%Y-%m-%d %H:%i:%s')
) as redPassCount, (select count(1) from tgod_photoalbum T2 where T2.modelID=T1.id and T2.type=3 and T2.status<>1
and T2.publishTime >=date_format(#{fromTime},'%Y-%m-%d %H:%i:%s')
and T2.publishTime <=date_format(#{toTime},'%Y-%m-%d %H:%i:%s')
) as redNoPassCount, (select count(1) from tgod_photoalbum T2 where T2.modelID=T1.id and T2.type=2 and T2.status=1
and T2.publishTime >=date_format(#{fromTime},'%Y-%m-%d %H:%i:%s')
and T2.publishTime <=date_format(#{toTime},'%Y-%m-%d %H:%i:%s')
) as dynamicPassCount, (select count(1) from tgod_photoalbum T2 where T2.modelID=T1.id and T2.type=2 and T2.status<>1
and T2.publishTime >=date_format(#{fromTime},'%Y-%m-%d %H:%i:%s')
and T2.publishTime <=date_format(#{toTime},'%Y-%m-%d %H:%i:%s')
) as dynamicNoPassCount from tgod_user T1
T1.id in (select T.modelID from tgod_photoalbum T where T.status=1 )
and (T1.id =#{modelname} or T1.nickname like '%${modelname}%')
limit #{start}, #{pageSize}

SELECT 
T1.id,
T1.type,
T1.modelID,
T1.modelName,
T1.roleid,
T1.rolename,
T1.publishTime,
(select count(1) from tgod_order where status=3
and  album_id=T1.id

and date_format(finish_time, '%Y%m ')=#{fromTime}

) as sales,
(select count(total_amount) from tgod_order where status=3 
and  album_id=T1.id

and date_format(finish_time, '%Y%m ')=#{fromTime}

) as profitTotal,
(select count(1) from tgod_album_view_log where  album_id=T1.id

and date_format(view_time, '%Y%m ')=#{fromTime}

) as viewCount
from tgod_photoalbum T1 left join tgod_albumcountinfo T2
on T1.id = T2.id
where T1.type<>1 and T1.status=1

      and  T1.id =#{id}


      and  T1.modelID =#{modelid}


       and T1.modelName like '%${modelname}%'



    limit #{start}, #{pageSize}

SELECT count(1) from tgod_photoalbum T1 where T1.type<>1 and T1.status=1
and T1.modelID =#{modelid}
and T1.modelName like '%${modelname}%'

SELECT date_format(T1.finish_time, '%Y') as year, date_format(T1.finish_time, '%m') as month, count(1) as sales, count(T1.total_amount) as profitTotal, max(T1.total_amount) as price, (select max(costPrice) from tgod_costtype where costdesc='动态成本') as costPrice, max(T2.level) level, count(T2.allocationAmount) userAmount, (select count(allocationAmount) as allocationAmount from tgod_orderprofit where profit_user_type is null and orderId=T1.id) as platformAmount, (select count(1) from tgod_album_view_log where album_id=T1.album_id
and date_format(T1.view_time, '%Y%m')=#{fromTime} ) as viewCount, max(T2.profitRatio) profitRatio from tgod_order T1 left join (select orderId,max(level) as level,max(profit_ratio) as profitRatio,count(allocationAmount) as allocationAmount from tgod_orderprofit where profit_user_type is not null group by orderId) T2 on T1.id=T2.orderId and T1.status=3
and T1.album_id=#{albumid}
and T1.buyer_id=#{modelid}
and date_format(T1.finish_time, '%Y%m')=#{fromTime}
group by date_format(T1.finish_time, '%Y%m')
limit #{start}, #{pageSize}

SELECT 
count(1) 
from(select max(T1.id) from  tgod_order T1 
where T1.status=3 

and T1.album_id=#{albumid}


and T1.buyer_id=#{modelid}


and date_format(T1.finish_time, '%Y%m')=#{fromTime}

 group by date_format(T1.finish_time, '%Y%m')) as T

 select 
 date_format(T1.view_time, '%Y') as year,
 date_format(T1.view_time, '%m') as month,
 count(1) as viewCount
 from 
 tgod_album_view_log T1 
 where  

 T1.album_id=#{albumid}


 and T1.model_id=#{modelid}


and date_format(T1.view_time, '%Y%m')=#{fromTime}

group by date_format(T1.view_time, '%Y%m')

    limit #{start}, #{pageSize}

SELECT 
count(1) 
from(select max(T1.id) from  tgod_album_view_log T1 
where  

 T1.album_id=#{albumid}


 and T1.model_id=#{modelid}


and date_format(T1.view_time, '%Y%m')=#{fromTime}

group by date_format(T1.view_time, '%Y%m')

select T1.id, T1.nickname, T5.amount, T5.cashout, T5.balance, T5.qingdou_amount as qingdouAmount, (select count(qingdou_amount) from tgod_records_qingdou where user_id=T1.id) as qingdouAmountconsum, (select count(1) from tgod_photoalbum where status=1 and type=1 and modelID=T1.id) as albumCount, (select count(1) from tgod_photoalbum where status=1 and type in('2','3') and modelID=T1.id) as dynamicCount, (select count(T5.allocationAmount) from (select T2.allocationAmount,T2.profit_user_id from tgod_orderprofit T2 left join tgod_photoalbum T3 on T2.product_id =T3.id and T3.type=1 ) as T5 where T5.profit_user_id=T1.id) as albumAmount, (select count(T5.allocationAmount) from (select T2.allocationAmount,T2.profit_user_id from tgod_orderprofit T2 left join tgod_photoalbum T3 on T2.product_id =T3.id and T3.type in(2,3) ) as T5 where T5.profit_user_id=T1.id) as dynamicAmount from tgod_user T1 left join tgod_user_account T5 on T1.id = T5.id and T1.user_group=1
and T1.id=#{id}
and T1.nickname=#{nickname}
limit #{start}, #{pageSize}

select count(1) from tgod_user T1 left join tgod_user_account T5 on T1.id = T5.id and T1.user_group=1
and T1.id=#{id}
and T1.nickname=#{nickname}
